#!/usr/bin/env node
import fs from 'fs';

import chalk from 'chalk';
import updateNotifier from 'update-notifier';
import yargs from 'yargs';

import GitHubWikiNotifier from './';
import * as env from './utils/env';
import pkg from '../package.json';

updateNotifier({pkg}).notify();

const argv = yargs
  .usage([
    'Usage: github-wiki-notifier <notifier>',
    '                            [--paylod=<path>]',
    '                            [--repository=<path>]',
    '                            [--clone-dir=<path>]',
    '                            [--max-message-length=<number>]',
    '                            [--dry-run]',
    '                            [--output-message]',
    '',
    'Notify diff of GitHub wiki to Chat'
  ].join('\n'))
  .example(
    'github-wiki-notifier hipchat --payload=/path/to/json',
    'Notify to HipChat from payload file in local'
  )
  .example(
    'github-wiki-notifier hipchat --repository=/path/to/repo',
    'Notify latest diff to HipChat from repo in local'
  )
  .example(
    'github-wiki-notifier hipchat',
    'Notify to HipChat from payload string of environment variable'
  )
  .option('p', {
    alias: 'payload',
    description: 'Set path to GitHub Webhook payload for gollum',
    type: 'string'
  })
  .option('r', {
    alias: 'repository',
    description: 'Set path to repository',
    type: 'string'
  })
  .option('c', {
    alias: 'clone-dir',
    description: 'Set path to cloning remote repository [default: ./<repo>]',
    type: 'string'
  })
  .option('max-message-length', {
    description: 'Set max length of message for contains diff',
    type: 'number'
  })
  .option('dry-run', {
    description: 'Set flag to disable notify',
    type: 'boolean'
  })
  .option('output-message', {
    description: 'Set flag to output notify message',
    type: 'boolean'
  })
  .help('help')
  .demand(1)
  .version(pkg.version)
  .detectLocale(false)
  .wrap(null)
  .strict()
  .argv;


/**
 * Get Payload object
 *
 * @param {string|undefined} payloadPath path to payload file
 * @return {Object|undefined}
 */
function getPayload(payloadPath) {
  let payload = payloadPath && fs.readFileSync(payloadPath, {encoding: 'utf-8'});
  payload = payload || env.get('WEBHOOK_PAYLOAD');
  return payload && JSON.parse(payload);
}


/**
 * Generate directory path for cloning remote directory
 *
 * @param {string|undefined} cloneDirPath path to directory for cloning remote repository
 * @param {Object|undefined} payload GitHub Webhook payload
 * @return {string|undefined}
 */
function getCloneDir(cloneDirPath, payload) {
  if (!cloneDirPath && payload) {
    return `./${payload.repository.name}`;
  }
  return cloneDirPath;
}


/**
 * execute
 *
 * @param {Object} args cli arguments
 */
function execute(args) {
  const [notifier] = args._;
  const payload = getPayload(args.payload);
  const opts = {
    payload,
    repository: args.repository,
    cloneDir: getCloneDir(args.cloneDir, payload),
    maxLength: args.maxMessageLength,
    dryRun: args.dryRun
  };
  const ghwn = new GitHubWikiNotifier(notifier);
  ghwn.notify(opts)
    .then(result => {
      if (!result) {
        console.log(chalk.bold('Nothing content for notification'));
      } else if (result.status === 'success') {
        if (args.outputMessage) {
          console.log(result.message);
        }
        console.log(chalk.bold.green(`Success notify to ${notifier}`));
      }
      process.exit();
    })
    .catch(e => {
      console.error(chalk.bold.red(e));
      process.exit(1);
    });
}

try {
  execute(argv);
} catch (e) {
  console.error(chalk.bold.red(e));
  process.exit(1);
}

import escapeHtml from 'escape-html';


export default class HTMLFomatter {

  constructor(maxLength) {
    this.maxLength = maxLength;
  }

  /**
   * create HTML for sending message
   *
   * @param {Object[]|string[]} diffs diff list
   * @param {Object|string} repository repository object in GitHub Webhook payload
   * @param {Object} [user] sender object in GitHub Webhook payload
   * @param {number} [maxLength] max words length for contains diff string
   * @return {string}
   */
  createMessage(diffs, repository, {user} = {}) {
    return diffs.map(d => {
      let header = '';
      let body = '';
      if (typeof d === 'string') {
        header = `Edited on ${repository}`;
        body = this.createDiff(d);
      } else {
        header = this.createHeader(d.page, user, repository);
        body = this.createDiff(d.diff);
      }
      if (body.length + header.length > this.maxLength) {
        return header;
      }
      return `${header}<br /><pre>${body}</pre>`;
    }).join('<br />');
  }

  /**
   * Create HTML for Header
   *
   * @param {Object} page page object in GitHub Webhook payload
   * @param {Object} user sender object in GitHub Webhook payload
   * @param {Object} repository repository object in GitHub Webhook payload
   * @return {string}
   */
  createHeader(page, user, repository) {
    const iconTag = `<img src="${user.avatar_url}" width="24" />`;
    const userTag = `<a href="${user.html_url}">${user.login}</a>`;
    const pageTag = `<a href="${page.html_url}">${page.page_name}</a>`;
    const repoTag = `<a href="${repository.html_url}">${repository.full_name}</a>`;
    return `${iconTag} ${userTag} ${page.action} ${pageTag} on ${repoTag}`;
  }

  /**
   * Convert Diff string to HTML
   *
   * @param {string} diffString diff string
   * @return {string}
   */
  createDiff(diffString) {
    return diffString.split('\n').map(line => {
      const escaped = escapeHtml(line);
      if (line.indexOf('-') === 0) {
        return `<span style="color:red">${escaped}</span>`;
      } else if (line.indexOf('+') === 0) {
        return `<span style="color:green">${escaped}</span>`;
      } else if (line.indexOf('diff') === 0) {
        return `<strong>${escaped}</strong>`;
      } else if (/^(?:index|@)/.test(line)) {
        return `<span style="color:gray">${escaped}</span>`;
      }
      return escaped;
    }).join('\n');
  }
}

import path from 'path';

import {
  getDiff,
  getDiffPages,
  getRepo,
  getRepoNameFromRemotePath,
  getRepoPathFromPayload
} from './repository';
import * as notifiers from './notifiers';


/**
 * Notify info of GitHub Wiki by specific notifier
 */
export default class GitHubWikiNotifier {

  /**
   * Constructor
   *
   * @param {string} notifierName notifier name
   */
  constructor(notifierName) {
    this.notifier = new notifiers[notifierName]();
  }

  /**
   * notify to service
   *
   * @param {Object} opts options
   * @param {Object} [opts.payload] GitHub Webhook payload
   * @param {string} [opts.repository] path to repository directory
   * @param {string} [opts.cloneDir] path to directory for cloning remote repository
   * @param {number} [opts.maxLength] max length
   * @param {boolean} [opts.dryRun] a flag for disable sending
   * @return {Promise}
   */
  notify(opts) {
    const {repository, payload} = opts;
    if (payload) {
      return this.notifyFromPayload(payload, opts);
    }
    return this.notifyFromRepository(repository, opts);
  }

  /**
   * notify from payload
   *
   * @param {Object} payload GitHub Webhook payload
   * @param {Object} opts options
   * @param {string} [opts.cloneDir] path to directory for cloning remote repository
   * @param {number} [opts.maxLength] max length
   * @param {boolean} [opts.dryRun] a flag for disable sending
   * @return {Promise}
   */
  notifyFromPayload(payload, opts = {}) {
    const {maxLength, cloneDir, dryRun} = opts;
    return getRepo(getRepoPathFromPayload(payload), {cloneDir})
      .then(({repo}) => getDiffPages(repo, ...payload.pages))
      .then(diffPages => diffPages.filter(dp => dp.diff))
      .then(diffPages => {
        if (diffPages.length > 0) {
          return this.notifier.notify(diffPages, payload.repository, {
            maxLength,
            dryRun,
            user: payload.sender
          });
        }
        return null;
      });
  }

  /**
   * notify from repository
   *
   * @param {string} repository path to repository directory
   * @param {Object} opts options
   * @param {number} [opts.maxLength] max length
   * @param {boolean} [opts.dryRun] a flag for disable sending
   * @return {Promise}
   */
  notifyFromRepository(repository, opts = {}) {
    const repoPath = repository || path.resolve('./');
    const {maxLength, dryRun} = opts;
    return getRepo(repoPath)
      .then(({repo}) => getDiff(repo))
      .then(diffString => {
        if (diffString) {
          return this.notifier.notify([diffString], getRepoNameFromRemotePath(repoPath), {
            maxLength, dryRun
          });
        }
        return null;
      });
  }
}

import HipchatNotify from 'hipchat-notify';

import HTMLFormatter from '../formatters/HTMLFormatter';
import * as env from '../utils/env';


/**
 * Notify to HipChat
 *
 * @param {Object[]|string[]} diffs diff list
 * @param {Object|string} repository repository object in GitHub Webhook payload
 * @param {Object} [user] sender object in GitHub Webhook payload
 * @param {number} [maxLength] repository object in GitHub Webhook payload
 * @param {Object} [opts] options
 * @return {Promise}
 */
export default class HipChatNotifier {

  /**
   * Constructor
   *
   * @param {Object} [opts] options
   */
  constructor(opts = {}) {
    const room = opts.room || env.get('HIPCHAT_ROOM');
    const roomToken = opts.token || env.get('HIPCHAT_ROOM_TOKEN');
    this.maxLength = 10000;
    this.hipchatNotify = new HipchatNotify(room, roomToken);
  }

  get service() {
    return 'HipChat';
  }

  /**
   * Send message
   *
   * @param {Object[]|string[]} diffs diff list
   * @param {Object|string} repository repository object in GitHub Webhook payload
   * @param {Object} [user] sender object in GitHub Webhook payload
   * @param {number} [maxLength] repository object in GitHub Webhook payload
   * @param {boolean} [dryRun] a flag for disable sending
   * @return {Promise}
   */
  notify(diffs, repository, {user, maxLength, dryRun} = {}) {
    const message = new HTMLFormatter(maxLength || this.maxLength)
      .createMessage(diffs, repository, {user});
    const result = {message, status: 'success'};
    if (dryRun) {
      return new Promise(resolve => resolve(result));
    }
    return new Promise((resolve, reject) => {
      this.hipchatNotify.info({message}, err => {
        if (err) {
          reject(err);
        } else {
          resolve(result);
        }
      });
    });
  }
}

import git from 'simple-git';

import * as env from './utils/env';


/**
 * Clone Repository
 *
 * @param {string} repoPath repository path
 * @param {string} cloneDir local path for clone
 * @return {Promise}
 */
export function cloneRepo(repoPath, cloneDir) {
  return new Promise((resolve, reject) => {
    git().clone(repoPath, cloneDir, err => {
      if (err) {
        reject(err);
      } else {
        resolve({repo: git(cloneDir)});
      }
    });
  });
}


/**
 * Get Repository name from remote path
 *
 * @param {string} repoPath repository path
 * @return {string}
 */
export function getRepoNameFromRemotePath(repoPath) {
  return repoPath.split('/').slice(-1)[0].split('.git')[0];
}


/**
 * Get Repository
 *
 * @param {string} repoPath repository path
 * @param {string} cloneDir local path for clone
 * @return {Promise}
 */
export function getRepo(repoPath, {cloneDir} = {}) {
  if (/^(?:(?:https?|ftps?|ssh|git|rsync):\/\/)/.test(repoPath)) {
    return cloneRepo(repoPath, cloneDir || getRepoNameFromRemotePath(repoPath));
  }
  return new Promise(resolve => {
    resolve({repo: git(repoPath)});
  });
}


/**
 * Get Diff Strings
 *
 * @param {Object} repo repository object of simple-git
 * @param {String[]} opts diff options for Git command
 * @return {Promise}
 */
export function getDiff(repo, ...opts) {
  return new Promise((resolve, reject) => {
    repo.show(opts, (err, diff) => {
      if (err) {
        reject(err);
      } else {
        resolve(diff);
      }
    });
  });
}


/**
 * Get Diff Strings for Pages
 *
 * @param {Object} repo repository object of simple-git
 * @param {Object[]} pages pages
 * @return {Promise}
 */
export function getDiffPages(repo, ...pages) {
  return Promise.all(pages.map(page => {
    return getDiff(repo, `${page.sha}`, '--format=%N').then(diff => ({page, diff}));
  }));
}


/**
 * Get remote repository path from payload object
 *
 * @param {Object} [payload] GitHub Webhook payload
 * @return {string}
 */
export function getRepoPathFromPayload(payload) {
  const repo = payload.repository;

  /**
   * @param {string} url repository path
   * @return {string}
   */
  function replace(url) {
    return url.replace(/\.git$/, '.wiki.git');
  }

  if (!repo.private) {
    return replace(repo.clone_url);
  }
  const token = env.get('GITHUB_TOKEN');
  if (token) {
    return replace(`https://${token}:@${repo.clone_url.split('https://')[0]}`);
  }
  return replace(repo.ssh_url);
}

const PREFIX = 'GHWIKINOTIFIER_';

/**
 * Get environment variables
 *
 * @param {string} name variable name
 * @return {string|undefined}
 */
export function get(name) {
  return process.env[`${PREFIX}${name}`];
}

import assert from 'power-assert';
import lib from '../src';

describe('github-wiki-notifier', () => {
  it('is defined', () => {
    assert(lib);
  });
});
